import * as React from 'react';
import {SafeAreaView,Image, Text, View, StyleSheet,Button,TextInput  } from 'react-native';


export default function CountryDetails(props) {

const styles = StyleSheet.create({
  
  
  card: {
    backgroundColor: 'white',
    borderRadius: 8,

    paddingVertical: 25,
    paddingHorizontal: 25,
    //width: '100%',
    margin: 10,

    shadowColor: 'gray',
    shadowOffset: { width: -2, height: 4 },
    shadowOpacity: 0.2,
    shadowRadius: 3,
  },
  flag: {
    width: 270,
    height: 180,
  },p:{
    marginVertical:5
  }
  
});


  return (
    <View style={styles.card}>
<Image style={styles.flag} source={props.Country.flags.png} 
      />
     <Text style={styles.p}>
        Capital : {props.Country.capital}
    </Text>
    <Text style={styles.p}>
        Population : {props.Country.population}
    </Text>
    <Text style={styles.p}>
        Latitude , Longitude  : {props.Country.latlng}
    </Text>
     <Button onPress={()=>this.FetchCountryInfo()}
       style={styles.formControl}  title="Capital Weather"  color="#841584"
        
    />
    </View>
  );
  

}
