import React, { Component } from 'react';
import {SafeAreaView,Image, Text, View, StyleSheet,Button,TextInput  } from 'react-native';









export class WeatherDetails extends Component {

  async componentDidMount(){
   await this.GetWeatherInfo();
  }
  async GetWeatherInfo() {
  
    const response = await fetch('http://api.weatherstack.com/current?access_key=4f6c569ded7d7d8688debb1fa1de9bb2&query=Washington');
    
    var temp = await response.json();
    temp=temp.current;
    console.log(temp);
    // alert(JSON.stringify(temp));
       this.setState({data:temp}); 
  }

   state={data:{weather_icons:'',temperature:0,precip:0,wind_speed:0}};
  render() {
    
    return (
      <View style={styles.card}>
      <Image style={styles.tinyLogo}
          source={this.state.data.weather_icons[0]}
        />
       <Text style={styles.p}>
          Temperature : {this.state.data.temperature}
      </Text>
      <Text style={styles.p}>
          Precipitation : {this.state.data.precip}
      </Text>
      <Text style={styles.p}>
          Wind Speed : {this.state.data.wind_speed}
      </Text>
      
      
      </View>
    );
  }
}

export default WeatherDetails//

const styles = StyleSheet.create({
  
  p:{
    marginVertical:5
  },
  
  card: {
    backgroundColor: 'white',
    borderRadius: 8,
paddingVertical: 25,
    paddingHorizontal: 25,
    //width: '100%',
    margin: 10,

    shadowColor: 'gray',
    shadowOffset: { width: -2, height: 4 },
    shadowOpacity: 0.2,
    shadowRadius: 3,
  },
   tinyLogo: {
    width: 50,
    height: 50,
  },
  logo: {
    width: 66,
    height: 58,
  }
  
});
