import React, { useState } from 'react';
import {SafeAreaView,Image, Text, View, StyleSheet,Button,TextInput  } from 'react-native';


var data = {weather_icons:'',temperature:0,precip:0,wind_speed:0};


export default function WeatherDetails(props) {

  var [data, updateData] = useState({weather_icons:'',temperature:0,precip:0,wind_speed:0});
 GetWeatherInfo();

  return (
    <View style={styles.card}>
    <Image style={styles.tinyLogo}
        source={data.weather_icons}
      />
     <Text style={styles.p}>
        Temperature : {data.temperature}
    </Text>
    <Text style={styles.p}>
        Precipitation : {data.precip}
    </Text>
    <Text style={styles.p}>
        Wind Speed : {data.wind_speed}
    </Text>
    
    
    </View>
  );
  

}
async function GetWeatherInfo() {
  
  const response = await fetch('http://api.weatherstack.com/current?access_key=4f6c569ded7d7d8688debb1fa1de9bb2&query=Washington');
  
  var temp = await response.json();
   temp=temp.current;
  console.log(temp);
   alert(JSON.stringify(temp));
     updateData(temp);
}







const styles = StyleSheet.create({
  
  p:{
    marginVertical:5
  },
  
  card: {
    backgroundColor: 'white',
    borderRadius: 8,
paddingVertical: 25,
    paddingHorizontal: 25,
    //width: '100%',
    margin: 10,

    shadowColor: 'gray',
    shadowOffset: { width: -2, height: 4 },
    shadowOpacity: 0.2,
    shadowRadius: 3,
  },
   tinyLogo: {
    width: 50,
    height: 50,
  },
  logo: {
    width: 66,
    height: 58,
  }
  
});
